#include<iostream>

using namespace std;
int main(){
    int num1, num2;
    cout<<"Enter the value of num1 : ";  /*'<<' is colled Insertion operater*/
    cin>>num1;

    cout<<"Enter the value of num2 : ";  /*'>>' is colled Extraction operater*/
    cin>>num2;

    cout<<"The sum is = "<<num1+num2;
    return 0;
}